import React,{useState ,useEffect} from 'react';
import { Row, Col } from 'antd';
import { connect } from "react-redux";
import addShape from "./actions";

import Webform from '../WebForm/index.js';
import Figures from "../Figures";




   
 const HomePage = ({renderobjects,dispatch}) => {
    
//    const [renderobjects, setrenderobjects] = useState([]);  
    const [dimensions,setdimensions] = useState([]);
    
    useEffect(()=>{
 
     console.log("useEffectHome",renderobjects);
    },[dimensions])
    
    const onChange = (value) => {
      const dimensionStore =  [];
      
     dispatch.addShape(value);
      
 
      
      console.log("OnChangeChange",renderobjects);
      
       setTimeout(() => {
           dimensionStore.push(renderobjects[0][1],renderobjects[0][2],renderobjects[0][3] , renderobjects[0][4],renderobjects[0][5],renderobjects[0][6]);
      
        if(renderobjects[renderobjects.length - 1][1][1] === "cone"){
          dimensionStore.push(renderobjects[0][7]);
        }
           
        else if(renderobjects[renderobjects.length - 1][1][1] === "cuboid"){
          dimensionStore.push(renderobjects[0][7],renderobjects[0][8],renderobjects[0][9])
        }
        setdimensions(renderobjects);
        dispatch.addShape(renderobjects); 
        
      },3000)
      console.log("Beforeonchange",dimensions); 
    };
   
    console.log("Afteronchange",renderobjects);
    return (
   <Row>
         <Col span={50}>
             <Webform 
             renderobjects={renderobjects}
             setrenderobjects={onChange} />
         
         </Col>
         

         <Col span={50}>
             <Figures 
             dimensions ={ dimensions}
             />
         </Col>
    </Row>
        
     );
     
 }
 const mapStateToProps = (state) => {
  const {renderobjects} = state.HomePage;

  return {
   renderobjects
  };
};

export default connect(mapStateToProps, null)(HomePage);
