import React,{useState ,useEffect} from 'react';
import { Row, Col } from 'antd';

import Webform from './WebForm/index.js';
import Figures from './Figures';



   
 const HomePage = () => {
    
    const [renderobjects, setrenderobjects] = useState([]);  
    const [dimensions,setdimensions] = useState([]);
    const [spherdimensions,setspherdimensions] = useState([]);
    
    
    
    const onChange = (value) => {
      const dimensionStore =  [];
      const sphereStore = [];
        
      setrenderobjects(value);
      
      console.log("OnChangeChange",renderobjects);
      const n = renderobjects.length;
      
      setTimeout(() => {  dimensionStore.push(renderobjects[0][1],renderobjects[0][2],renderobjects[0][3] , renderobjects[0][4],renderobjects[0][5],renderobjects[0][6]);
      
        if(renderobjects[renderobjects.length - 1][1][1] === "cone"){
          dimensionStore.push(renderobjects[0][7]);
        }
           
        else if(renderobjects[renderobjects.length - 1][1][1] === "cuboid"){
          dimensionStore.push(renderobjects[0][7],renderobjects[0][8],renderobjects[0][9])
        }
        setdimensions(dimensionStore);
  
        
      },3000)
     if(renderobjects.length === 2){
      setTimeout(() => {  sphereStore.push(renderobjects[1][1],renderobjects[1][2],renderobjects[1][3] , renderobjects[1][4],renderobjects[1][5],renderobjects[1][6]);
      
        if(renderobjects[renderobjects.length - 1][1][1] === "cone"){
          sphereStore.push(renderobjects[1][7]);
        }
           
        else if(renderobjects[renderobjects.length - 1][1][1] === "cuboid"){
          sphereStore.push(renderobjects[1][7],renderobjects[1][8],renderobjects[1][9])
        }
        setspherdimensions(sphereStore);
  
        
      },3000)
    }
      console.log("Beforeonchange",dimensions); 
    };

    console.log("Afteronchange",dimensions);
    console.log("SphereStore",spherdimensions);
    return (
   <Row>
         <Col span={50}>
             <Webform 
             renderobjects={renderobjects}
             setrenderobjects={onChange} />
         
         </Col>
         

         <Col span={50}>
             <Figures 
             dimensions ={ dimensions}
             spherdimensions = {spherdimensions}
             />
         </Col>
    </Row>
        
     );
     
 }

 export default HomePage;
