import React, { useState , useEffect } from "react";
import ReactDOM from "react-dom";
import "antd/dist/antd.css";
import "./index.css";
import {
  Form,
  Input,
  Select,
  InputNumber,
  Checkbox,
  Button,
  Layout,
  Space,
  message,
  Alert
} from "antd";
const { Option } = Select;


const { Content, Footer } = Layout;
const Webform = () => {
  const [componentSize, setComponentSize] = useState("default");
  const [ConformationText, setConformationText] = useState(false );
  const [visibleSphere, setvisibleSphere] = useState(true);
  const [visibleCube, setvisibleCube] = useState(true);
  const [visibleCone, setvisibleCone] = useState(true);
  const [visibleCuboid, setvisibleCuboid] = useState(true);
 
  const [visibleShape, setvisibleShape] = useState('empty');

  const [shapes,setshapes] = useState({ 
  });
  
  
  const [renderobjects, setrenderobjects] = useState([]);  
  const PrevRender= () =>{
    return(
      <Form.Item label = "Render" name="render">
      <Select>
      {renderobjects.map((value) => {return(<Form.Item label = "Render" name="render">
             <Option name = {value}>
               {value}
             </Option>                   
    </Form.Item>)
    })}
      </Select>                   
      </Form.Item>
    

    );

  };
  
  const onFormLayoutChange = ({ size }) => {
    setComponentSize(size);
  };
  const onFinish = values => {
    console.log('Success:', values);
    setConformationText(true);
   const renderStore = renderobjects; 
   renderStore.push(  Object.entries(values).map((entry) => entry));
//    console.log(renderStore);

    renderStore.forEach((value,index) => {console.log(value,index)});
    setrenderobjects(renderStore);

  };

  const onFinishFailed = errorInfo => {
    console.log('Failed:', errorInfo);
  };
  const dropdownHandler = value => {
     
    setvisibleShape(value);
     
   };
   console.log(visibleCube);



  const Sphereform = () => {
    return(
  
  <Form.Item label="Enter the  radius" name="Radius" rules={[
     {
      required: visibleShape === 'sphere'? true : false,
      message: 'Please enter the radius!',
     },

  ]}hidden = {visibleShape === 'sphere'? false : true}>
  <InputNumber step={0.1}/>
  </Form.Item>

    );
};
const Coneheightform = () => {
  return (
    <Form.Item label="Enter the height " name="height" rules={[
       {
        required: !visibleCone,
        message: 'Please enter the height!',
       },

    ]}hidden = {visibleCone}>
    <InputNumber step={0.1}/>
    </Form.Item>

    
  );
};
const Coneradiusform = () =>{
return (
     <Form.Item label="Enter the radius" name="coneradius" rules={[
       {
         required: !visibleCone,
         message: 'Please enter the radius',
       },

    ]} hidden = {visibleCone}>
    <InputNumber step={0.1}/>
    </Form.Item>
 
)
};
const Cubeform = () => {
return (
<Form style={{ContentAlign:"left"}}
