import React, { Component}  from "react";
import * as THREE from "three";
import OrbitControls from "three-orbitcontrols";
import e1 from "./textures/2_no_clouds_4k.jpg";
import e2 from "./textures/elev_bump_4k.jpg";
import e3 from "./textures/water_4k.png";

class Figures extends Component {
  componentDidMount() {
    console.log("didmount",this.props.dimensions);
    this.sceneSetup();
    this.addModels();
    this.start();
    window.addEventListener("resize", this.handleWindowResize);
  }

  componentDidUpdate(prevProps, prevState) {
    console.log("prevProps ", prevProps);
    console.log("prevState ", prevState);

    if (this.props.dimensions !== prevProps.dimensions) {

      console.log("Positions",this.props.dimensions);
      this.addShapes();
    }
  }

  componentWillUnmount() {
    window.removeEventListener("resize", this.handleWindowResize);
    window.cancelAnimationFrame(this.requestID);
    this.controls.dispose();
  }

  sceneSetup = () => {
    const width = this.el.clientWidth;
    const height = this.el.clientHeight;

    this.scene = new THREE.Scene();

    this.camera = new THREE.PerspectiveCamera(75, width / height, 0.1, 1000);
    this.camera.position.z = 30;
    this.camera.position.y = 5;

    this.controls = new OrbitControls(this.camera, this.el);

    this.renderer = new THREE.WebGLRenderer({ antialias: true });
    this.renderer.setClearColor("#000000");
    this.renderer.setSize(width, height);
    this.el.appendChild(this.renderer.domElement);

    this.scene.add(new THREE.AmbientLight(0x333333));
    var light = new THREE.PointLight(0xffffff, 1);
    light.position.set(10, 10, 10);
     
    this.scene.add(light);
    var lights = new THREE.AmbientLight(0x333333)
    this.scene.add(lights);
    lights.position.set(40,30,30);
    light = new THREE.DirectionalLight(0xffffff, 1);
    this.scene.add(light);
  };

  addShapes = () =>{
   
    if(this.props.dimensions.length !== 0) {
     
    
     if(this.props.dimensions[0][1] === "cube")
     {  
      var geometry1 = new THREE.BoxGeometry(this.props.dimensions[5][1],this.props.dimensions[5][1],this.props.dimensions[5][1]);
      var material1 = new THREE.MeshLambertMaterial({color:this.props.dimensions[1][1]});
      var boxMesh = new THREE.Mesh(geometry1, material1);
      
      this.scene.add(boxMesh);
    
     
     boxMesh.position.set(this.props.dimensions[2][1],this.props.dimensions[3][1],this.props.dimensions[4][1]);
      
     
    }
    else if(this.props.dimensions[0][1] === "sphere")
    {
      var geometrys = new THREE.SphereGeometry(this.props.dimensions[5][1],32,32);
      var materials = new THREE.MeshLambertMaterial({color:this.props.dimensions[1][1]});
      var sphereMesh = new THREE.Mesh(geometrys,materials);
      this.scene.add(sphereMesh);
      sphereMesh.position.set(this.props.dimensions[2][1],this.props.dimensions[3][1],this.props.dimensions[4][1]);
    }
    else if(this.props.dimensions[0][1] === "cone")
    {
      var geometryc = new THREE.ConeGeometry(this.props.dimensions[6][1],this.props.dimensions[5][1],64);
      var materialc = new THREE.MeshBasicMaterial({color:this.props.dimensions[1][1]});
      var coneMesh = new THREE.Mesh(geometryc, materialc);
      this.scene.add(coneMesh);
      coneMesh.position.set(this.props.dimensions[2][1],this.props.dimensions[3][1],this.props.dimensions[4][1]);
    } 

    else if(this.props.dimensions[0][1] === "cuboid")
    {
     
      var geometrycu = new THREE.BoxGeometry(this.props.dimensions[6][1],this.props.dimensions[7][1],this.props.dimensions[5][1]);
      var materialcu = new THREE.MeshLambertMaterial({color:this.props.dimensions[1][1]});
      var cuboidMesh = new THREE.Mesh(geometrycu, materialcu);
      
      this.scene.add(cuboidMesh);
    
     
     cuboidMesh.position.set(this.props.dimensions[2][1],this.props.dimensions[3][1],this.props.dimensions[4][1]);
     
    }
    
    }
  };
  addModels = () => {
    var geometry = new THREE.SphereGeometry(10, 35, 35);
    var material = new THREE.MeshPhongMaterial({
      map: THREE.ImageUtils.loadTexture(e1),
      bumpMap: THREE.ImageUtils.loadTexture(e2),
      bumpScale: 0.005,
      specularMap: THREE.ImageUtils.loadTexture(e3),
      specular: new THREE.Color("grey"),
    });
    this.earthMesh = new THREE.Mesh(geometry, material);
    this.scene.add(this.earthMesh);
    this.earthMesh.position.set(0, 0, 0);
    
    
    };
  start = () => {
    //this.earthMesh.rotation.x += 0.01;
    this.earthMesh.rotation.y += 0.01;
    
    this.renderer.render(this.scene, this.camera);

    // The window.requestAnimationFrame() method tells the browser that you wish to perform
    // an animation and requests that the browser call a specified function
    // to update an animation before the next repaint
    this.requestID = window.requestAnimationFrame(this.start);
  };
  handleWindowResize = () => {
    const width = this.el.clientWidth;
    const height = this.el.clientHeight;

    this.renderer.setSize(width, height);
    this.camera.aspect = width / height;

    // Note that after making changes to most of camera properties you have to call
    // .updateProjectionMatrix for the changes to take effect.
    this.camera.updateProjectionMatrix();
  };

  render() {
    return (
      <div
        style={{ width: "1000px", height: "692px" }}
        ref={(ref) => {
          this.el = ref;
        }}
      />
    );
  }
}

export default Figures;
