import React, { Component } from "react";
import * as THREE from "three";
//import { MTLLoader, OBJLoader } from "three-obj-mtl-loader";
import OrbitControls from "three-orbitcontrols";
//import e1 from "C:/Users/Dev/Documents/react/my-app/src/textures/2_no_clouds_4k.jpg";

class Figures extends Component {
  componentDidMount() {
    const width = this.mount.clientWidth;
    const height = this.mount.clientHeight;
    this.scene1 = new THREE.Scene();
    this.scene2 = new THREE.Scene();

    //Add Renderer
    this.renderer = new THREE.WebGLRenderer({ antialias: true });
    this.renderer.setClearColor("#ffffff");
    this.renderer.setSize(width, height);
    this.mount.appendChild(this.renderer.domElement);

    //add Camera
    this.camera1 = new THREE.PerspectiveCamera(75, width / height, 0.1, 1000);
    this.camera1.position.z = 20;
    this.camera1.position.y = 5;

    //Camera Controls
    const controls = new OrbitControls(this.camera1, this.renderer.domElement);

    //LIGHTS
    this.scene1.add(new THREE.AmbientLight(0x333333));
    var light = new THREE.PointLight(0xFFFFFF,1);
    light.position.set( 10, 10, 10 );
    this.scene1.add( light );

    var light = new THREE.DirectionalLight(0xFFFFFF,1);
     this.scene1.add(light);
    // var lights = [];
    // lights[0] = new THREE.PointLight(0x304ffe, 1, 0);
    // lights[1] = new THREE.PointLight(0xffffff, 1, 0);
    // lights[2] = new THREE.PointLight(0xffffff, 1, 0);
    // lights[0].position.set(10, 10, 10);
    // lights[1].position.set(100, 200, 100);
    // lights[2].position.set(-100, -200, -100);
    // this.scene1.add(lights[0]);
    // this.scene1.add(lights[1]);
    // this.scene1.add(lights[2]);

    // this.scene2.add(lights[0]);
    // this.scene2.add(lights[1]);
    // this.scene2.add(lights[2]);
    
    //Simple Box with WireFrame
    this.addModels();

    this.renderScene();
    //start animation
    this.start();
  }

  addModels() {
    // -----Step 1--------
    // earth =  new THREE.Mesh(
    //   new THREE.SphereGeometry(1, 35, 35),
    //   new THREE.MeshPhongMaterial({
    //     map: THREE.ImageUtils.loadTexture('textures/2_no_clouds_4k.jpg'),
    //     bumpMap: THREE.ImageUtils.loadTexture('textures/elev_bump_4k.jpg'),
    //     bumpScale:   0.005,
    //     specularMap: THREE.ImageUtils.loadTexture('textures/water_4k.png'),
    //     specular: new THREE.Color('grey') })
    // );
 
    var geometry = new THREE.SphereGeometry(10, 35, 35);
    var material = new THREE.MeshPhongMaterial({
      color: "#0F0"
    });
    this.cube = new THREE.Mesh(geometry, material);
    this.scene1.add(this.cube);
    this.cube.position.set=(0,0,0);
    const textureLoader = new THREE.TextureLoader();
   // this.cube.material.map = textureLoader.load(e1);

    //  geometry = new THREE.BoxGeometry(5, 5, 5);
    //  material = new THREE.MeshBasicMaterial({
    //   color: "#0F0"
    // });
    // this.cube2 = new THREE.Mesh(geometry, material);
    // this.scene1.add(this.cube2);
    // this.cube2.position.set = (80,30,50);

  }

  componentWillUnmount() {
    this.stop();
    this.mount.removeChild(this.renderer.domElement);
  }
  start = () => {
    if (!this.frameId) {
      this.frameId = requestAnimationFrame(this.animate);
    }
  };
  stop = () => {
    cancelAnimationFrame(this.frameId);
  };
  animate = () => {
   
    if (this.cube) this.cube.rotation.y += 0.01;
    this.renderScene();
    this.frameId = window.requestAnimationFrame(this.animate);
  };
  renderScene = () => {
    if (this.renderer) this.renderer.render(this.scene1, this.camera1);
  };

 
  render() {
    return (
      <div
        style={{ width: "1000px", height: "700px" }}
        ref={mount => {
          this.mount = mount;
        }}
      />
    );
  }
}
export default Figures;
